import React, { useState } from "react";
import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({ appointments, contacts, addAppointment }) => {
  const [title, setTitle] = useState("");
  const [contact, setContact] = useState({}); // Update initial state to an empty object
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [reason, setReason] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const actualContact = contacts.find((contactElement) => contactElement.name === contact.name);
    const newAppointment = {
      title,
      contact: actualContact,
      date,
      time,
      reason,
    };
    addAppointment(newAppointment);
    setTitle("");
    setContact({});
    setDate("");
    setTime("");
    setReason("");
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm
          contacts={contacts}
          title={title}
          setTitle={setTitle}
          contact={contact.name} // Pass the name of the contact
          setContact={setContact}
          date={date}
          setDate={setDate}
          time={time}
          setTime={setTime}          
          setReason={setReason}
          handleSubmit={handleSubmit}
        />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
        <TileList tiles={appointments} />
      </section>
    </div>
  );
};
