import React from "react";

export const PetPicker = ({ pets, owner, generatePetID, onChange, value }) => {
    return (
        <select onChange={onChange} value={value}>
        <option value="">No Pet Selected</option>
        {pets.map((pet) => {
            if (pet.owner === owner) {
            return (
                <option key={pet.id} value={pet.id}>
                {pet.name}, {pet.owner}, {pet.id}
                </option>
            );
            }
            return null;
        })}
        </select>
    );
};
