import React, { useState } from "react";
import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({ appointments, pets, contacts, addAppointment, generatePetID }) => {
  const [name, setName] = useState("");
  const [contact, setContact] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [reason, setReason] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if all fields are filled
    if (name && contact && date && time && reason) {
      // Add appointment
      addAppointment(name, contact, date, time, reason);

      // Clear form data
      setName("");
      setContact("");
      setDate("");
      setTime("");
      setReason("");
    } else {
      alert("Please fill in all fields");
    }
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm
          name={name}
          contact={contact}
          date={date}
          time={time}
          reason={reason}
          setName={setName}
          setContact={setContact}
          setDate={setDate}
          setTime={setTime}
          setReason={setReason}
          handleSubmit={handleSubmit}
          pets={pets}
          contacts={contacts}
          generatePetID={generatePetID}
        />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
        <TileList data={appointments} />
      </section>
    </div>
  );
};
