import './App.css';
import React, { useState } from "react";
import { RouterProvider, createBrowserRouter, createRoutesFromElements, Route, Navigate } from "react-router-dom";
import Root, { ROUTES } from "./components/root/Root";
import { AppointmentsPage } from "./containers/appointmentsPage/AppointmentsPage";
import { ContactsPage } from "./containers/contactsPage/ContactsPage";
import { PetInfoPage } from "./containers/etInfoPage/PetInfoPage"; // Import the PetInfoPage component

function App() {
  const [contacts, setContacts] = useState([]);
  const [appointments, setAppointments] = useState([]);
  const [pets, setPets] = useState([]); // Add state for pets

  const addContact = (name, phoneNumber, email) => {
    const newContact = {
      name: name,
      phoneNumber: phoneNumber,
      email: email,
    };
    setContacts([...contacts, newContact]);
  };

  const addAppointment = (name, contact, date, time) => {
    const newAppointment = {
      name: name,
      contact: contact,
      date: date,
      time: time,
    };
    setAppointments([...appointments, newAppointment]);
  };

  const addPet = (pet) => {
    setPets([...pets, pet]);
  };

  const router = createBrowserRouter(
    createRoutesFromElements(
      <Route path="/" element={<Root />}>
        <Route index element={<Navigate to={ROUTES.CONTACTS} replace />} />
        <Route
          path={ROUTES.CONTACTS}
          element={<ContactsPage contacts={contacts} addContact={addContact} />}
        />
        <Route
          path={ROUTES.APPOINTMENTS}
          element={
            <AppointmentsPage
              appointments={appointments}
              contacts={contacts}
              addAppointment={addAppointment}
            />
          }
        />
        <Route
          path={ROUTES.PETINFO} // Add route for PetInfoPage
          element={<PetInfoPage pets={pets} addPet={addPet} />}
        />
      </Route>
    )
  );

  return <RouterProvider router={router} />;
}

export default App;
